import * as WEColor from 'WEColor';

export var scriptProperties = createScriptProperties()
	.addCheckbox({
		name: 'audioRainboweffect',
		label: 'Audio Rainbow Effect',
		value: true
	})
	.addCheckbox({
		name: 'rainbowEffect',
		label: 'Rainbow Effect',
		value: false
	})
	.addSlider({
		name: 'rainbowSpeed',
		label: 'Rainbow Speed',
		value: 1,
		min: 0.1,
		max: 5,
		integer: false
	})
	.addSlider({
		name: 'rainbowSaturation',
		label: 'Rainbow Saturation',
		value: 1,
		min: 0.1,
		max: 10,
		integer: false
	})
	.addSlider({
		name: 'rainbowBrightness',
		label: 'Rainbow Brightness',
		value: 1,
		min: 0.1,
		max: 10,
		integer: false
	})
	.addSlider({
		name: 'audioSourcefrequency',
		label: 'Audio Source Frequency (Lower values for bass, higher for treble.) ',
		value: 0,
		min: 0,
		max: 15,
		integer: true
	})
	.finish();

//let rainbowSpeed = 1;
//let rainbowSaturation = 1;
//let rainbowBrightness = 1;
let frequencyResolution = 16; // Must be 16, 32 or 64 per channel.
//let sourceFrequency = 0; // Must be smaller than frequencyResolution. Lower values for bass, higher for treble.
let smoothingRate = 11;
let audioBuffer = engine.registerAudioBuffers(frequencyResolution);
var smoothValue = 0;


/**
 * @param {Vec3} value
 */
export function update(value) {
	if (scriptProperties.rainbowEffect == true) {
	value = WEColor.hsv2rgb({
		x: Date.now() / 2000 * scriptProperties.rainbowSpeed % 1,
		y: scriptProperties.rainbowSaturation,
		z: scriptProperties.rainbowBrightness
	});
}
let audioDelta = audioBuffer.average[scriptProperties.audioSourcefrequency] - smoothValue;
	if (scriptProperties.audioRainboweffect == true) {
	smoothValue += audioDelta * engine.frametime * smoothingRate;
	smoothValue = Math.min(1.0, smoothValue);
	value = WEColor.hsv2rgb({
		x: smoothValue,
		y: scriptProperties.rainbowSaturation,
		z: scriptProperties.rainbowBrightness
	});
	}
	return value;
}
