	.addSlider({
		name: 'hoScale',
		label: 'Hover scale multiplier',
		value: 1.5,
		min: 0.1,
		max: 2,
		integer: false
	})
	.addSlider({
		name: 'speed',
		label: 'Scale change speed',
		value: 10,
		min: 1,
		max: 100,
		integer: false
	})
	.addSlider({
		name: 'audioDistortScale',
		label: 'Audio responsiveness multiplier',
		value: 1.1,
		min: 0,
		max: 10,
		integer: false
	})
	.finish();

/* ────────────────────────────────────────────────
   Audio Settings
────────────────────────────────────────────────── */
const frequencyResolution = 16; // Must be 16, 32, or 64 per channel
const sourceFrequency = 15; // Lower = bass, higher = treble
const smoothingRate = 20;   // Controls audio response smoothing

const audioBuffer = engine.registerAudioBuffers(frequencyResolution);
let smoothValue = 0.5;

/* ────────────────────────────────────────────────
   State Variables
────────────────────────────────────────────────── */
let hover = false;
let baseScale = new Vec3(1, 1, 1);
let targetScale = 1.0;
let currentScale = new Vec3(1, 1, 1);

/* ────────────────────────────────────────────────
   User Property Updates
────────────────────────────────────────────────── */
export function applyUserProperties(changedUserProperties) {
	// Handle style-based scaling
	if (changedUserProperties.style !== undefined) {
		const style = changedUserProperties.style;
		targetScale = (style === 'style1') ? 1.0 : 1.0;
		baseScale = new Vec3(targetScale, targetScale, targetScale);
	}

	// Update hover and speed-related settings
	if (scriptProperties.hoScale !== undefined) {
		// no-op, but ensures values are read
	}
}

/* ────────────────────────────────────────────────
   Initialization
────────────────────────────────────────────────── */
export function init(value) {
	baseScale = value;
	currentScale = value;
	return value;
}

/* ────────────────────────────────────────────────
   Update Loop (called every frame)
────────────────────────────────────────────────── */
export function update(value) {
	// Smooth audio responsiveness
	let audioDelta = audioBuffer.average[sourceFrequency] - smoothValue;
	smoothValue += audioDelta * engine.frametime * smoothingRate;
	smoothValue = Math.min(1.0, Math.max(0.0, smoothValue));

	const audioDistort = 1 + (smoothValue * scriptProperties.audioDistortScale);
	const speed = scriptProperties.speed / 100;

	// Determine target scale based on hover and style
	const hoverScale = hover ? scriptProperties.hoScale : 1.0;
	const desiredScale = targetScale * hoverScale * audioDistort;

	// Smoothly interpolate (lerp) toward target
	const lerpFactor = speed * engine.frametime * 60;
	const newScaleValue = WEMath.mix(currentScale.x, desiredScale, lerpFactor);

	currentScale = new Vec3(newScaleValue, newScaleValue, newScaleValue);
	return currentScale;
}

/* ────────────────────────────────────────────────
   Mouse Hover Events
────────────────────────────────────────────────── */
