	// Check if the 'style' property was the one that changed
	if (changedUserProperties.style !== undefined) {
		let style = changedUserProperties.style; // Get the new value ("style1" or "style2")

		// Update the global target variables based on the new style
		if (style === "style1") {
			targetX = initial.x;
			targetY = initial.y;
		} else {
			targetX = initial.x + scriptProperties.offsetX;
			targetY = initial.y + scriptProperties.offsetY;
		}
	}
}

