pointsize.script:
'use strict';

let defaultSize = 0;
let currentScale = 1;

export function init(value) {
    defaultSize = value;
}

export function applyUserProperties(props) {
    const p = props.textscale;
    if (!p) return;

    // Try multiple known formats Wallpaper Engine can send
    let raw =
        p.value ??
        p.float ??
        p.number ??
        p.text ??
        p; // sometimes p itself is numeric

    raw = Number(raw);

    if (!Number.isFinite(raw)) {
        console.log('[textscale] Could not parse value from', p);
        return;
    }

    // treat 100 as 1.0
    currentScale = raw > 10 ? raw / 100 : raw;
    if (currentScale < 0) currentScale = 0;

    console.log('[textscale] final scale =', currentScale, '(raw =', raw, ')');
}

export function update(value) {
    const base = defaultSize || value || 0;
    return base * currentScale;
}

