export var scriptProperties = createScriptProperties()
	.addSlider({
		name: 'hoScale',
		label: 'Hover over scale multiplier',
		value: 1.5,
		min: 0.1,
		max: 2,
		integer: false
	})
	.addSlider({
		name: 'speed',
		label: 'Scale change speed',
		value: 10,
		min: 1,
		max: 100,
		integer: false
	})
	.finish();

let initScale, newScale, hover = false, propertyCheck = true, speed;

export function update(value) {
	if (hover) 
	{
		value = new Vec3(WEMath.mix(value.x, newScale.x, speed), WEMath.mix(value.y, newScale.y, speed), WEMath.mix(value.z, newScale.z, speed));
	}
	else
	{
		value = new Vec3(WEMath.mix(value.x, initScale.x, speed), WEMath.mix(value.y, initScale.y, speed), WEMath.mix(value.z, initScale.z, speed));
	}
	return value;
}

export function init(value) {
	initScale = value;
	return value;
}

export function cursorEnter(event) {
	hover = true;
}

export function cursorLeave(event) {
	hover = false;
}

export function applyUserProperties(changedUserProperties) {
	if (scriptProperties.hoScale != undefined && propertyCheck)
	{
		newScale = new Vec3(initScale.multiply(scriptProperties.hoScale));
		speed = scriptProperties.speed/100;
		propertyCheck = false;
	}

}

