export let __workshopId = '2877039332';

// NOTE:
// Don't forget to make a 'Checkbox' property and connect the 'Tint color' to that new property

// Primary color of the Media Cover
var newColor;

var window = {};

export var scriptProperties = createScriptProperties()
    .addCheckbox({
        name: 'dynamicBar',
        label: 'Dynamic Bar',
        value: false
    })
    .addColor({
        name: 'barColor',
        label: 'Bar Color',
        value: (window.wallpaperPropertiesListener = {
                    applyUserProperties: function(properties) {
                    properties.barcolor;
                    }
                })
    })
    .finish();


/**
 * @param {Vec3} value - for property 'color'
 * @return {Vec3} - update current property value
 */
export function update(value) {
    if (scriptProperties.dynamicBar == true) {
        value = new Vec3(newColor);
    } else {
        // Changes color based on property 'Title Color'
        value = new Vec3(scriptProperties.barColor);
    }
    return value;
}

/**
 * @param {MediaThumbnailEvent} event
 */
export function mediaThumbnailChanged(event) {
    if (event.hasThumbnail == true) {
        newColor = event.primaryColor;
    }
}

