let initScale, newScale, hover = false, propertyCheck = true, speed;

/**
 * Configure these to control smoothing and the frequency.
 */
let frequencyResolution = 16; // Must be 16, 32 or 64 per channel.
let sourceFrequency = 15; // Must be smaller than frequencyResolution. Lower values for bass, higher for treble.
let smoothingRate = 20;

/**
 * This creates a permanent link to the audio response data.
 */
let audioBuffer = engine.registerAudioBuffers(frequencyResolution);
var smoothValue = 0.5;

export function update(value) {
	let audioDelta = audioBuffer.average[sourceFrequency] - smoothValue;
	smoothValue += audioDelta * engine.frametime * smoothingRate;
	smoothValue = Math.min(1.0, smoothValue);
	let audioDistort = 1 + (smoothValue * scriptProperties.audioDistortScale);

	if (hover) 
	{
		value = new Vec3(WEMath.mix(value.x, newScale.x * audioDistort, speed), WEMath.mix(value.y, newScale.y * audioDistort, speed), WEMath.mix(value.z, newScale.z * audioDistort, speed));
	}
	else
	{
		value = new Vec3(WEMath.mix(value.x, initScale.x * audioDistort, speed), WEMath.mix(value.y, initScale.y * audioDistort, speed), WEMath.mix(value.z, initScale.z * audioDistort, speed));
	}

	return value;
}

