  const yearFull = d.getFullYear().toString();
  const yearShort = yearFull.slice(-2);

  const monthNumericPad = (monthIndex + 1).toString().padStart(2, '0');
  const monthNumeric = (monthIndex + 1).toString();
  const monthAbbrev = ['Jan','Feb','Mar','Apr','May','Jun','Jul','Aug','Sep','Oct','Nov','Dec'][monthIndex];
  const monthFull = ['January','February','March','April','May','June','July','August','September','October','November','December'][monthIndex];

  const dayPad = dateOfMonth.toString().padStart(2, '0');
  const dayNoPad = dateOfMonth.toString();

  const dowAbbrev = ['Sun','Mon','Tue','Wed','Thu','Fri','Sat'][dayIndex];
  const dowFull = ['Sunday','Monday','Tuesday','Wednesday','Thursday','Friday','Saturday'][dayIndex];

  // Replacement map (keys uppercase)
  const map = {
    'YYYY': yearFull,
    'YEAR': yearFull,
    'YY': yearShort,

    'MONTH': monthFull,
    'MON': monthAbbrev,
    'MM': monthNumericPad,
    'M': monthNumeric,

    'DAYOFWEEK': dowFull,
    'DOW': dowAbbrev,

    'DD': dayPad,
    'DAY': dayNoPad,
    'D': dayNoPad
  };

  // Build a single regex that matches any supported token (longer tokens first)
  const tokenKeys = Object.keys(map).sort((a,b) => b.length - a.length);
  const tokenRegex = new RegExp(tokenKeys.join('|'), 'gi');

  // First convert escaped newlines \n -> real newline
  let result = rawFormat.replace(/\\n/gi, '\n');

  // Single-pass replacement using replacer function  prevents re-replacement inside inserted values
  result = result.replace(tokenRegex, function(match) {
    const key = match.toUpperCase();
    return (key in map) ? map[key] : match;
  });

  // Prepend day-of-week if requested (keeps original behaviour)
  if (scriptProperties.showDay) {
    const separator = scriptProperties.alignVertical ? '\n' : ' ';
    result = dowFull + separator + result;
  }

  return result;
}
